#%% Order Dates
import pandas as pd

# User input example
dates = ['06 Jul 1933', '05 Jun 1933', '28 Feb 1921']

# Split dates by day,month,year
split_dates = [s.split(' ') for s in dates]
df_sdates = pd.DataFrame(split_dates, columns = ['d','m','y'])
df_sdates['input'] = dates

# dictionary to map month to number
dic_m2n = dict(zip(['Jan','Feb','Mar','Apr','May','Jun',
                    'Jul','Aug','Sep','Oct','Nov','Dec'],
                   range(1,13)))

# Parse strings to ints
df_sdates['m'] = df_sdates['m'].map(dic_m2n)
df_sdates[['d','y']] = df_sdates[['d','y']].astype(int)

# MultiIndex
tmpidx = pd.MultiIndex.from_frame(df_sdates[['y','m','d']])

# Dates as dataframe with multiindex
df_dates = pd.DataFrame(dates, columns=['input'], index=tmpidx)

# Sorted dates in chronologically order
df_dates.sort_index()
df_dates.sort_values(by=[('y','m','d')])

#%% Palindrome Permutation
str_input = 'taco cat is' # 'anita lava la tina'
u_chars = set(str_input.replace(' ',''))
n_chars = [(c,str_input.count(c)) for c in u_chars]
odds = []
evens = []
res = False
for elem in n_chars:
    if elem[1]%2 == 0:
        evens.append(elem)
    else:
        odds.append(elem)
    if len(odds) <= 1:
        res = True
    

#%% Longest Substring wo Replacement
class tmpFunc:
    def getPossibleNextChars(self, dic_chars, left_char):
    
        return [c for c in dic_chars.keys() 
                if any([n<m for n in dic_chars[left_char[-1]] 
                        for m in dic_chars[c]]) and c not in left_char]
    
def getSubstrings(s: str) -> list:
    # Appearance order of each char
    enum_s = [(c,i) for i,c in enumerate(s)]

    # Unique char with order of appearance
    u_s = dict(zip(set(s), [[] for x in range(len(set(s)))]))
    for elem in enum_s:
        u_s[elem[0]].append(elem[1])
        
    # If only one unique char, then it is it
    if len(u_s)<=1:
        return list(u_s.keys())

    # Loop through all unique ordered chars
    list_subtrings = []
    for k in u_s.keys():
        # Get the 2-char substrings
        substr = k
        tmpac = tmpFunc().getPossibleNextChars(u_s,k)
        tmpac = [c for c in tmpac if substr+c in s]
        lsubtr = [substr+q for q in tmpac if substr+q in s]
        # Get all further n-char substrings
        while lsubtr != []:
            # Concatenate following chars to get substrings
            tmpdic = {}
            for i in lsubtr: 
                tmpdic.setdefault(i, tmpFunc().getPossibleNextChars(u_s,i))
            for r,v in tmpdic.items(): 
                tmpdic[r] = [q for q in v if r+q in s]
            keys2remove = [c for c,w in tmpdic.items() if w == []]
            # Here: tmpdic keys tells the substrings 
            [list_subtrings.append(q) for q in tmpdic.keys()]
            [tmpdic.pop(c) for c in keys2remove]
            lsubtr = [r+t[0] for r in tmpdic.keys() 
                                for t in tmpdic.values() if r+t[0] in s]
    
    return list_subtrings
    
def lengthOfLongestSubstring(s: str) -> int:
    # Check for string having already unique chars
    setstr = set(s)
    if len(setstr) == len(s):
        return len(s)
    
    # All possible subtrings
    lstSubstrings = getSubstrings(s)
    
    # Length of all substrings
    substr_len = [len(c) for c in lstSubstrings]
    
    return max(substr_len)

tests = ['ehespkahcyng', 'qpxrjxkitzyxacbhhkicqc', 
         'gehmbfqmozbpripibusbezagafqtypz', 'abcabcbb', 'bbbbb', 'abba',
         'pwwkew']
for strinput in tests:
    print(f'{strinput}: {lengthOfLongestSubstring(strinput)}')



